import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const CourseList = () => {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/courses/')
            .then(response => setCourses(response.data))
            .catch(error => console.error('Error fetching courses', error));
    }, []);

    // Function to truncate the description text
    const truncateText = (text, maxLength) => {
        if (text.length <= maxLength) {
            return text;
        }
        return text.substring(0, maxLength) + '...';
    };

    return (
        <div className="course-list-container">
            <div className="courses-header">
                <h1 className="courses-title">Courses List</h1>
                <Link to="/add-course" className="btn btn-add-course">Add Course</Link>
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Instructor</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {courses.map(course => (
                        <tr key={course.id}>
                            <td>{course.title}</td>
                            <td>
                                {truncateText(course.description, 100)}{' '}
                                {course.description.length > 100 && (
                                    <span className="read-more">
                                        <Link to={`/course/${course.id}`}>Read More</Link>
                                    </span>
                                )}
                            </td>
                            <td>{course.instructor}</td>
                            <td className="action-buttons">
                                <Link to={`/edit-course/${course.id}`} className="btn btn-update">Update</Link>
                                <Link to={`/course/${course.id}`} className="btn btn-view">View</Link>
                                <button onClick={() => {/* handleDelete(course.id) */}} className="btn btn-delete">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default CourseList;
