import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';

const CourseForm = () => {
    const [course, setCourse] = useState({ title: '', description: '', instructor: '' });
    const history = useHistory();
    const { courseId } = useParams();

    useEffect(() => {
        if (courseId) {
            // Fetch course details for editing
            axios.get(`/api/courses/${courseId}`)
                .then(response => setCourse(response.data))
                .catch(error => console.error('Error fetching course details', error));
        }
    }, [courseId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setCourse(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const method = courseId ? 'put' : 'post';
        const url = courseId ? `/api/courses/${courseId}` : '/api/courses';

        axios[method](url, course)
            .then(() => history.push('/'))
            .catch(error => console.error('Error saving course', error));
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title</label>
                <input
                    name="title"
                    value={course.title}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Description</label>
                <textarea
                    name="description"
                    value={course.description}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Instructor</label>
                <input
                    name="instructor"
                    value={course.instructor}
                    onChange={handleChange}
                    required
                />
            </div>
            <button type="submit" className="btn btn-success">Save Course</button>
        </form>
    );
};

export default CourseForm;
